def shift(s, n):
    begin = ord('a')
    relative_pos = ord(s) - begin
    relative_pos = (relative_pos + n) % 26
    absolute_pos = relative_pos + begin
    return chr(absolute_pos)


def shiftingLetters(S, shifts):
    S = list(S)
    max_sum = sum(shifts)
    last_sum = 0
    for index, value in enumerate(shifts):
        shift_times = max_sum - last_sum
        last_sum += value
        new_char = shift(S[index], shift_times)
        S[index] = new_char
    return "".join(S)


